  1→//! Derive for `Dummy` trait.
  2→
  3→use std::borrow::Cow;
  4→
  5→use proc_macro2::TokenStream;
  6→use quote::quote;
  7→
  8→use crate::{
  9→    codegen::Codegen,
 10→    schema::{
 11→        Def, EnumDef, Schema, StructDef, TypeDef, TypeId,
 12→        extensions::{
 13→            dummy::{Alloc, MinVariant},
 14→            layout::GetLayout,
 15→        },
 16→    },
 17→    utils::format_cow,
 18→};
 19→
 20→use super::{Derive, StructOrEnum, define_derive};
 21→
 22→/// Derive for `Dummy` trait.
 23→pub struct DeriveDummy;
 24→
 25→define_derive!(DeriveDummy);
 26→
 27→impl Derive for DeriveDummy {
 28→    fn trait_name(&self) -> &'static str {
 29→        "Dummy"
 30→    }
 31→
 32→    fn trait_has_lifetime(&self) -> bool {
 33→        true
 34→    }
 35→
 36→    fn crate_name(&self) -> &'static str {
 37→        "oxc_allocator"
 38→    }
 39→
 40→    fn prelude(&self) -> TokenStream {
 41→        quote! {
 42→            #![allow(unused_variables, clippy::inline_always)]
 43→
 44→            ///@@line_break
 45→            use oxc_allocator::{Allocator, Dummy};
 46→        }
 47→    }
 48→
 49→    /// Initialize `dummy.alloc` on structs and enums
 50→    fn prepare(&self, schema: &mut Schema, _codegen: &Codegen) {
 51→        for type_id in schema.types.indices() {
 52→            let alloc = calculate_alloc(type_id, schema);
 53→            assert!(alloc != Alloc::NOT_CALCULATED);
 54→        }
 55→    }
 56→
 57→    fn derive(&self, type_def: StructOrEnum, schema: &Schema) -> TokenStream {
 58→        match type_def {
 59→            StructOrEnum::Struct(struct_def) => generate_impl_for_struct(struct_def, schema),
 60→            StructOrEnum::Enum(enum_def) => generate_impl_for_enum(enum_def, schema),
 61→        }
 62→    }
 63→}
 64→
 65→/// Calculate number and size of allocations required to construct a dummy for a type.
 66→///
 67→/// Before calculation, set `alloc` to [`Alloc::CALCULATING`].
 68→/// If `calculate_alloc` is called again for that type, it returns [`Alloc::NOT_CALCULATED`],
 69→/// which indicates the type has a circular dependency.
 70→/// This should only happen while calculating the cost of an enum. That variant will not be chosen.
 71→fn calculate_alloc(type_id: TypeId, schema: &mut Schema) -> Alloc {
 72→    let type_def = &mut schema.types[type_id];
 73→    #[expect(clippy::match_same_arms)]
 74→    match type_def {
 75→        TypeDef::Struct(struct_def) => {
 76→            let alloc = &mut struct_def.dummy.alloc;
 77→            if *alloc == Alloc::NOT_CALCULATED {
 78→                *alloc = Alloc::CALCULATING;
 79→                calculate_alloc_for_struct(type_id, schema)
 80→            } else if *alloc == Alloc::CALCULATING {
 81→                Alloc::NOT_CALCULATED
 82→            } else {
 83→                *alloc
 84→            }
 85→        }
 86→        TypeDef::Enum(enum_def) => {
 87→            let alloc = &mut enum_def.dummy.alloc;
 88→            if *alloc == Alloc::NOT_CALCULATED {
 89→                *alloc = Alloc::CALCULATING;
 90→                calculate_alloc_for_enum(type_id, schema)
 91→            } else if *alloc == Alloc::CALCULATING {
 92→                Alloc::NOT_CALCULATED
 93→            } else {
 94→                *alloc
 95→            }
 96→        }
 97→        // Primitives own no further allocation beyond themselves
 98→        TypeDef::Primitive(_) => Alloc::ZERO,
 99→        // `Option`s are `None` in dummy nodes
100→        TypeDef::Option(_) => Alloc::ZERO,
101→        // `Box`es have cost of the inner type plus an allocation of the type itself
102→        TypeDef::Box(box_def) => {
103→            let inner_type_id = box_def.inner_type_id;
104→            let mut alloc = calculate_alloc(inner_type_id, schema);
105→            if alloc != Alloc::NOT_CALCULATED {
106→                let inner_type = &schema.types[inner_type_id];
107→                alloc.bytes_64 += inner_type.layout_64().size;
108→                alloc.bytes_32 += inner_type.layout_32().size;
109→                alloc.count += 1;
110→            }
111→            alloc
112→        }
113→        // `Vec`s are empty in dummy nodes
114→        TypeDef::Vec(_) => Alloc::ZERO,
115→        // `Cell`s only own allocations if their inner type does
116→        TypeDef::Cell(cell_def) => calculate_alloc(cell_def.inner_type_id, schema),
117→        // Pointers cannot be created in dummies.
118→        // Pointers don't implement `Dummy`, so attempting to implement `Dummy` on a type containing
119→        // pointers will generate code which will not compile.
120→        TypeDef::Pointer(_) => Alloc::ZERO,
121→    }
122→}
123→
124→/// Calculate number and size of allocations required to construct a dummy for a struct.
125→///
126→/// Equals the total of allocations for all the struct's fields.
127→fn calculate_alloc_for_struct(type_id: TypeId, schema: &mut Schema) -> Alloc {
128→    let mut alloc = Alloc::ZERO;
129→    for field_index in schema.struct_def(type_id).field_indices() {
130→        let field_type_id = schema.struct_def(type_id).fields[field_index].type_id;
131→        let field_alloc = calculate_alloc(field_type_id, schema);
132→        if field_alloc == Alloc::NOT_CALCULATED {
133→            alloc = field_alloc;
134→            break;
135→        }
136→        alloc.bytes_64 += field_alloc.bytes_64;
137→        alloc.bytes_32 += field_alloc.bytes_32;
138→        alloc.count += field_alloc.count;
139→    }
140→
141→    schema.struct_def_mut(type_id).dummy.alloc = alloc;
142→
143→    alloc
144→}
145→
146→/// Calculate number and size of allocations required to construct a dummy for an enum.
147→///
148→/// Select the enum variant which has the lowest allocation cost.
149→///
150→/// Choice is made on these criteria, in order:
151→/// * Smallest number of bytes allocated on 64-bit systems.
152→/// * Smallest number of bytes allocated on 32-bit systems.
153→/// * Smallest number of individual allocations.
154→///
155→/// Record both the allocation cost, and which variant has the smallest cost.
156→fn calculate_alloc_for_enum(type_id: TypeId, schema: &mut Schema) -> Alloc {
157→    // All `#[ast]` enums are `#[repr(u8)]` or `#[repr(C, u8)]` so cannot have 0 variants
158→    let mut alloc = Alloc::NOT_CALCULATED;
159→    let mut min_variant = MinVariant::default();
160→
161→    // Own variants
162→    for variant_index in schema.enum_def(type_id).variant_indices() {
163→        let variant_type_id = schema.enum_def(type_id).variants[variant_index].field_type_id;
164→        if let Some(variant_type_id) = variant_type_id {
165→            let variant_alloc = calculate_alloc(variant_type_id, schema);
166→            if variant_alloc < alloc {
167→                alloc = variant_alloc;
168→                min_variant = MinVariant::Own(variant_index);
169→            }
170→        } else {
171→            alloc = Alloc::ZERO;
172→            min_variant = MinVariant::Own(variant_index);
173→            break;
174→        }
175→    }
176→
177→    // Inherited variants
178→    for inherits_index in schema.enum_def(type_id).inherits_indices() {
179→        let inherits_type_id = schema.enum_def(type_id).inherits[inherits_index];
180→        let inherits_alloc = calculate_alloc_for_enum(inherits_type_id, schema);
181→        if inherits_alloc < alloc {
182→            alloc = inherits_alloc;
183→            min_variant = schema.enum_def(inherits_type_id).dummy.min_variant;
184→            if let MinVariant::Own(variant_index) = min_variant {
185→                min_variant = MinVariant::Inherited(inherits_type_id, variant_index);
186→            }
187→        }
188→    }
189→
190→    let dummy = &mut schema.enum_def_mut(type_id).dummy;
191→    dummy.alloc = alloc;
192→    dummy.min_variant = min_variant;
193→
194→    alloc
195→}
196→
197→/// Generate `Dummy` impl for struct.
198→fn generate_impl_for_struct(struct_def: &StructDef, schema: &Schema) -> TokenStream {
199→    let fields = struct_def.fields.iter().map(|field| {
200→        let field_ident = field.ident();
201→        // Special case: node_id uses NodeId::DUMMY instead of Dummy::dummy
202→        if field.name() == "node_id" {
203→            quote!(#field_ident: oxc_syntax::node::NodeId::DUMMY)
204→        } else {
205→            quote!(#field_ident: Dummy::dummy(allocator))
206→        }
207→    });
208→
209→    let value = quote! {
210→        Self {
211→            #(#fields),*
212→        }
213→    };
214→
215→    generate_impl(struct_def.name(), &struct_def.ty(schema), &value, struct_def.dummy.alloc, false)
216→}
217→
218→/// Generate `Dummy` impl for enum.
219→fn generate_impl_for_enum(enum_def: &EnumDef, schema: &Schema) -> TokenStream {
220→    let variant = match enum_def.dummy.min_variant {
221→        MinVariant::Own(variant_index) => &enum_def.variants[variant_index],
222→        MinVariant::Inherited(inherited_type_id, variant_index) => {
223→            &schema.enum_def(inherited_type_id).variants[variant_index]
224→        }
225→    };
226→
227→    let variant_ident = variant.ident();
228→    let (value, should_inline) = if variant.field_type(schema).is_some() {
229→        (quote!( Self::#variant_ident(Dummy::dummy(allocator)) ), false)
230→    } else {
231→        (quote!( Self::#variant_ident ), true)
232→    };
233→
234→    generate_impl(
235→        enum_def.name(),
236→        &enum_def.ty(schema),
237→        &value,
238→        enum_def.dummy.alloc,
239→        should_inline,
240→    )
241→}
242→
243→/// Generate `Dummy` impl for a type.
244→fn generate_impl(
245→    name: &str,
246→    ty: &TokenStream,
247→    value: &TokenStream,
248→    alloc: Alloc,
249→    should_inline: bool,
250→) -> TokenStream {
251→    let comment1 = format!(" Create a dummy [`{name}`].");
252→    let comment2 = if alloc.bytes_64 == 0 {
253→        Cow::Borrowed(" Does not allocate any data into arena.")
254→    } else {
255→        let s = if alloc.count > 1 { "s" } else { "" };
256→        format_cow!(" Has cost of making {} allocation{s} ({} bytes).", alloc.count, alloc.bytes_64)
257→    };
258→
259→    let inline = if should_inline { quote!( #[inline(always)] ) } else { quote!() };
260→
261→    quote! {
262→        impl<'a> Dummy<'a> for #ty {
263→            #[doc = #comment1]
264→            #[doc = ""]
265→            #[doc = #comment2]
266→            #inline
267→            fn dummy(allocator: &'a Allocator) -> Self {
268→                #value
269→            }
270→        }
271→    }
272→}
273→

