                }
            }
        })
        .unwrap_or_default();

    let methods = quote! { #semantic_methods #node_id_methods };

    if methods.is_empty() {
        return None;
    }

    let struct_ty = struct_def.ty_anon(schema);
    Some(quote! {
        ///@@line_break
        impl #struct_ty {
            #methods
        }
    })
}

