            let get_doc2 = format!(" Only use this method on a post-semantic AST where [`{inner_type_name}`]s are always defined.");
            let get_doc3 = format!(" Panics if `{field_name}` is [`None`].");

            let get_method = quote! {
                #[doc = #get_doc1]
                ///
                #[doc = #get_doc2]
                ///
                /// # Panics
