impl GetAddress for PropertyKeyKind<'_> {
    #[inline] // This should boil down to a single instruction
    fn address(&self) -> Address {
        match *self {
            Self::Static(ident) => ident.unstable_address(),
            Self::Private(ident) => ident.unstable_address(),
