export default {
  meta: {
    name: "eslint-plugin-plugin4-name-from-rule",
  },
  rules: {
    rule: {
      create(context) {
        return {
          FunctionDeclaration(node) {
            context.report({
              message: `id: ${context.id}`,
              node,
            });
          },
        };
      },
    },
  },
};
